query DetailTestsQuery {
  show(id: "anderson-fine-art-gallery-flickinger-collection") {
    ...Detail_show
    __id
  }
}

fragment Detail_show on Show {
  _id
  id
  name
  description
  city
  is_local_discovery
  location {
    id
    address
    address_2
    displayDaySchedules {
      days
      hours
    }
    __id
    ...LocationMap_location
  }
  images {
    id
  }
  ...ShowHeader_show
  ...ShowArtworksPreview_show
  ...ShowArtistsPreview_show
  ...Shows_show
  counts {
    artworks
  }
  status
  partner {
    __typename
    ... on ExternalPartner {
      name
      __id
    }
    ... on Partner {
      name
      type
    }
    ... on Node {
      __id
    }
  }
  __id
}

fragment LocationMap_location on Location {
  __id
  id
  city
  address
  address_2
  postal_code
  summary
  coordinates {
    lat
    lng
  }
  day_schedules {
    start_time
    end_time
    day_of_week
  }
}

fragment ShowHeader_show on Show {
  id
  _id
  __id
  name
  press_release
  is_followed
  exhibition_period
  status
  is_local_discovery
  partner {
    __typename
    ... on Partner {
      name
      id
      href
    }
    ... on ExternalPartner {
      name
      __id
    }
    ... on Node {
      __id
    }
  }
  images {
    url
    aspect_ratio
  }
  followedArtists(first: 2) {
    edges {
      node {
        artist {
          name
          href
          id
          _id
          __id
        }
      }
    }
  }
  artists {
    name
    href
    id
    _id
    __id
  }
}

fragment ShowArtworksPreview_show on Show {
  __id
  artworks(size: 6) {
    ...GenericGrid_artworks
    __id
  }
  counts {
    artworks
  }
}

fragment ShowArtistsPreview_show on Show {
  _id
  id
  artists {
    _id
    id
    href
    ...ArtistListItem_artist
    __id
  }
  __id
}

fragment Shows_show on Show {
  nearbyShows(first: 20) {
    edges {
      node {
        ...ShowItem_show
        __id
      }
    }
  }
  __id
}

fragment ShowItem_show on Show {
  _id
  id
  name
  exhibition_period
  images {
    url
    aspect_ratio
  }
  partner {
    __typename
    ... on ExternalPartner {
      name
      __id
    }
    ... on Partner {
      name
    }
    ... on Node {
      __id
    }
  }
  __id
}

fragment ArtistListItem_artist on Artist {
  __id
  _id
  id
  name
  is_followed
  nationality
  birthday
  deathday
  image {
    url
  }
}

fragment GenericGrid_artworks on Artwork {
  __id
  id
  image {
    aspect_ratio
  }
  ...Artwork_artwork
}

fragment Artwork_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  is_biddable
  is_acquireable
  is_offerable
  id
  sale {
    is_auction
    is_live_open
    is_open
    is_closed
    display_timely_at
    __id
  }
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}
