query FavoriteShowsQuery {
  me {
    ...Shows_me
    __id
  }
}

fragment Shows_me on Me {
  followsAndSaves {
    shows(first: 10) {
      pageInfo {
        startCursor
        endCursor
        hasPreviousPage
        hasNextPage
      }
      edges {
        node {
          __id
          ...ShowItemRow_show
          __typename
        }
        cursor
      }
    }
  }
  __id
}

fragment ShowItemRow_show on Show {
  id
  _id
  __id
  is_followed
  name
  isStubShow
  partner {
    __typename
    ... on Partner {
      name
      profile {
        image {
          url(version: "square")
        }
        __id
      }
    }
    ... on Node {
      __id
    }
    ... on ExternalPartner {
      __id
    }
  }
  href
  exhibition_period
  status
  cover_image {
    url
    aspect_ratio
  }
  is_fair_booth
  start_at
  end_at
}
