query GeneArtworksGridQuery(
  $__id: ID!
  $count: Int!
  $cursor: String
  $sort: String
) {
  node(__id: $__id) {
    __typename
    ... on FilterArtworks {
      ...GeneArtworksGrid_filtered_artworks_1RfMLO
    }
    __id
  }
}

fragment GeneArtworksGrid_filtered_artworks_1RfMLO on FilterArtworks {
  __id
  artworks: artworks_connection(first: $count, after: $cursor, sort: $sort) {
    pageInfo {
      hasNextPage
      startCursor
      endCursor
    }
    edges {
      node {
        id
        __id
        image {
          aspect_ratio
        }
        ...ArtworkGridItem_artwork
        __typename
      }
      cursor
    }
  }
}

fragment ArtworkGridItem_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  is_biddable
  is_acquireable
  is_offerable
  id
  sale {
    is_auction
    is_live_open
    is_open
    is_closed
    display_timely_at
    __id
  }
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}
