query GlobalMapRefetchQuery(
  $near: Near
) {
  viewer {
    ...GlobalMap_viewer_279V1T
  }
}

fragment GlobalMap_viewer_279V1T on Viewer {
  city(near: $near) {
    name
    coordinates {
      lat
      lng
    }
    shows(discoverable: true, first: 50, sort: START_AT_ASC) {
      edges {
        node {
          id
          _id
          __id
          name
          status
          is_followed
          cover_image {
            url
          }
          location {
            coordinates {
              lat
              lng
            }
            __id
          }
          type
          start_at
          end_at
          partner {
            __typename
            ... on Partner {
              name
              type
            }
            ... on ExternalPartner {
              name
              __id
            }
            ... on Node {
              __id
            }
          }
          ...SavedShowItemRow_show
        }
      }
    }
    fairs(first: 10) {
      edges {
        node {
          id
          name
          counts {
            partners
          }
          location {
            coordinates {
              lat
              lng
            }
            __id
          }
          image {
            image_url
            aspect_ratio
            url
          }
          profile {
            icon {
              id
              href
              height
              width
              url(version: "square140")
            }
            __id
            id
            name
          }
          start_at
          end_at
          __id
        }
      }
    }
  }
}

fragment SavedShowItemRow_show on Show {
  id
  _id
  __id
  is_followed
  name
  partner {
    __typename
    ... on Partner {
      name
    }
    ... on ExternalPartner {
      name
      __id
    }
    ... on Node {
      __id
    }
  }
  href
  status
  cover_image {
    url
    aspect_ratio
  }
  start_at
  end_at
}
