query MapRendererQuery(
  $near: Near!
) {
  viewer {
    ...GlobalMap_viewer_279V1T
  }
}

fragment GlobalMap_viewer_279V1T on Viewer {
  city(near: $near) {
    name
    coordinates {
      lat
      lng
    }
    shows(discoverable: true, first: 50, sort: START_AT_ASC) {
      edges {
        node {
          id
          name
          images {
            url
          }
          location {
            coordinates {
              lat
              lng
            }
            __id
          }
          type
          start_at
          end_at
          partner {
            __typename
            ... on Partner {
              name
              type
            }
            ... on ExternalPartner {
              name
              __id
            }
            ... on Node {
              __id
            }
          }
          __id
        }
      }
    }
    fairs(size: 10) {
      id
      name
      __id
    }
  }
}
