query MapRendererQuery(
  $citySlug: String!
  $maxInt: Int!
) {
  viewer {
    ...GlobalMap_viewer_3La17j
  }
}

fragment GlobalMap_viewer_3La17j on Viewer {
  city(slug: $citySlug) {
    name
    slug
    coordinates {
      lat
      lng
    }
    sponsoredContent {
      introText
      artGuideUrl
      featuredShows {
        id
        _id
        __id
        name
        status
        isStubShow
        href
        is_followed
        exhibition_period
        cover_image {
          url
        }
        location {
          coordinates {
            lat
            lng
          }
          __id
        }
        type
        start_at
        end_at
        partner {
          __typename
          ... on Partner {
            name
            type
          }
          ... on Node {
            __id
          }
          ... on ExternalPartner {
            __id
          }
        }
      }
      shows(first: 1, sort: START_AT_ASC) {
        totalCount
      }
    }
    upcomingShows: shows(includeStubShows: true, status: UPCOMING, dayThreshold: 14, first: $maxInt, sort: START_AT_ASC) {
      edges {
        node {
          id
          _id
          __id
          isStubShow
          name
          status
          href
          is_followed
          exhibition_period
          cover_image {
            url
          }
          location {
            coordinates {
              lat
              lng
            }
            __id
          }
          type
          start_at
          end_at
          partner {
            __typename
            ... on Partner {
              name
              type
            }
            ... on Node {
              __id
            }
            ... on ExternalPartner {
              __id
            }
          }
        }
      }
    }
    shows(includeStubShows: true, status: RUNNING, first: $maxInt, sort: PARTNER_ASC) {
      edges {
        node {
          id
          _id
          __id
          isStubShow
          name
          status
          href
          is_followed
          exhibition_period
          cover_image {
            url
          }
          location {
            coordinates {
              lat
              lng
            }
            __id
          }
          type
          start_at
          end_at
          partner {
            __typename
            ... on Partner {
              name
              type
              profile {
                image {
                  url(version: "square")
                }
                __id
              }
            }
            ... on Node {
              __id
            }
            ... on ExternalPartner {
              __id
            }
          }
        }
      }
    }
    fairs(first: $maxInt, status: CURRENT, sort: START_AT_ASC) {
      edges {
        node {
          id
          name
          exhibition_period
          counts {
            partners
          }
          location {
            coordinates {
              lat
              lng
            }
            __id
          }
          image {
            image_url
            aspect_ratio
            url
          }
          profile {
            icon {
              id
              href
              height
              width
              url(version: "square140")
            }
            __id
            id
            name
          }
          start_at
          end_at
          __id
        }
      }
    }
  }
}
