query QueryRenderersFairQuery(
  $fairID: String!
) {
  fair(id: $fairID) {
    ...Fair_fair
    __id
  }
}

fragment Fair_fair on Fair {
  id
  __id
  ...FairDetail_fair
  organizer {
    website
  }
  about
  ticketsLink
}

fragment FairDetail_fair on Fair {
  ...FairHeader_fair
  id
  _id
  name
  hours
  isActive
  location {
    ...LocationMap_location
    coordinates {
      lat
      lng
    }
    __id
  }
  organizer {
    website
  }
  about
  ticketsLink
  profile {
    name
    __id
  }
  sponsoredContent {
    activationText
    pressReleaseUrl
  }
  shows: shows_connection(first: 5) {
    pageInfo {
      hasNextPage
      startCursor
      endCursor
    }
    edges {
      cursor
      node {
        id
        _id
        artworks_connection(first: 4) {
          edges {
            node {
              id
              __id
            }
          }
        }
        ...FairBoothPreview_show
        __id
        __typename
      }
    }
  }
  __id
}

fragment FairHeader_fair on Fair {
  id
  _id
  name
  formattedOpeningHours
  counts {
    artists
    partners
  }
  followed_content {
    artists {
      name
      href
      id
      _id
      __id
    }
    galleries {
      _id
      name
      __id
    }
  }
  partner_names: shows_connection(first: 2) {
    edges {
      node {
        id
        partner {
          __typename
          ... on Partner {
            profile {
              name
              id
              _id
              __id
            }
          }
          ... on Node {
            __id
          }
          ... on ExternalPartner {
            __id
          }
        }
        __id
      }
    }
  }
  artists_names: artists(first: 3) {
    edges {
      node {
        name
        href
        id
        _id
        __id
      }
    }
  }
  image {
    image_url
    aspect_ratio
    url
  }
  profile {
    icon {
      id
      href
      height
      width
      url(version: "square140")
    }
    __id
    id
    name
    is_followed
  }
  start_at
  end_at
  exhibition_period
  __id
}

fragment LocationMap_location on Location {
  __id
  id
  city
  address
  address_2
  postal_code
  summary
  coordinates {
    lat
    lng
  }
  day_schedules {
    start_time
    end_time
    day_of_week
  }
  openingHours {
    __typename
    ... on OpeningHoursArray {
      schedules {
        days
        hours
      }
    }
    ... on OpeningHoursText {
      text
    }
  }
}

fragment FairBoothPreview_show on Show {
  id
  _id
  name
  is_fair_booth
  counts {
    artworks
  }
  partner {
    __typename
    ... on Partner {
      name
      href
      id
      _id
      __id
      profile {
        _id
        is_followed
        __id
      }
    }
    ... on Node {
      __id
    }
    ... on ExternalPartner {
      __id
    }
  }
  fair {
    name
    __id
  }
  cover_image {
    url
  }
  location {
    display
    __id
  }
  artworks_connection(first: 4) {
    edges {
      node {
        ...GenericGrid_artworks
        __id
      }
    }
  }
  __id
}

fragment GenericGrid_artworks on Artwork {
  __id
  id
  image {
    aspect_ratio
  }
  ...ArtworkGridItem_artwork
}

fragment ArtworkGridItem_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  is_biddable
  is_acquireable
  is_offerable
  id
  sale {
    is_auction
    is_live_open
    is_open
    is_closed
    display_timely_at
    __id
  }
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}
