query QueryRenderersWorksForYouQuery(
  $selectedArtist: String!
) {
  viewer {
    ...WorksForYou_viewer_2hRnfI
  }
}

fragment WorksForYou_viewer_2hRnfI on Viewer {
  me {
    followsAndSaves {
      notifications: bundledArtworksByArtist(sort: PUBLISHED_AT_DESC, first: 10) {
        pageInfo {
          hasNextPage
          endCursor
        }
        edges {
          node {
            __id
            ...Notification_notification
            __typename
          }
          cursor
        }
      }
    }
    __id
  }
  selectedArtist: artist(id: $selectedArtist) {
    id
    href
    name
    image {
      resized(height: 80, width: 80) {
        url
      }
    }
    artworks(sort: published_at_desc, size: 6) {
      ...GenericGrid_artworks
      __id
    }
    __id
  }
}

fragment Notification_notification on FollowedArtistsArtworksGroup {
  summary
  artists
  artworks {
    artists(shallow: true) {
      href
      __id
    }
    ...GenericGrid_artworks
    __id
  }
  image {
    resized(height: 80, width: 80) {
      url
    }
  }
  __id
}

fragment GenericGrid_artworks on Artwork {
  __id
  id
  image {
    aspect_ratio
  }
  ...ArtworkGridItem_artwork
}

fragment ArtworkGridItem_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  is_biddable
  is_acquireable
  is_offerable
  id
  sale {
    is_auction
    is_live_open
    is_open
    is_closed
    display_timely_at
    __id
  }
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}
