query SaleQuery(
  $saleID: String!
) {
  sale(id: $saleID) {
    ...Sale_sale
    __id
  }
}

fragment Sale_sale on Sale {
  id
  name
  ...Header_sale
  ...SaleArtworksGrid_sale
  __id
}

fragment Header_sale on Sale {
  name
  cover_image {
    href
  }
  __id
}

fragment SaleArtworksGrid_sale on Sale {
  __id
  saleArtworks: sale_artworks_connection(first: 10) {
    pageInfo {
      hasNextPage
      startCursor
      endCursor
    }
    edges {
      node {
        artwork {
          id
          __id
          image {
            aspect_ratio
          }
          ...ArtworkGridItem_artwork
        }
        __id
        __typename
      }
      cursor
    }
  }
}

fragment ArtworkGridItem_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  is_biddable
  is_acquireable
  is_offerable
  id
  sale {
    is_auction
    is_live_open
    is_open
    is_closed
    display_timely_at
    __id
  }
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}
