query SalesQuery {
  viewer {
    ...Sales_viewer
  }
}

fragment Sales_viewer on Viewer {
  sales(live: true, is_auction: true, size: 100, sort: TIMELY_AT_NAME_ASC) {
    ...SaleListItem_sale
    href
    live_start_at
    __id
  }
  ...LotsByFollowedArtists_viewer
}

fragment SaleListItem_sale on Sale {
  id
  name
  href
  is_open
  is_live_open
  live_url_if_open
  start_at
  end_at
  registration_ends_at
  live_start_at
  display_timely_at
  cover_image {
    url(version: "large")
    aspect_ratio
  }
  __id
}

fragment LotsByFollowedArtists_viewer on Viewer {
  sale_artworks: sale_artworks(first: 10, live_sale: true, is_auction: true, include_artworks_by_followed_artists: true) {
    pageInfo {
      endCursor
      hasNextPage
    }
    edges {
      cursor
      node {
        artwork {
          ...GenericGrid_artworks
          __id
        }
        __id
        __typename
      }
    }
  }
}

fragment GenericGrid_artworks on Artwork {
  __id
  id
  image {
    aspect_ratio
  }
  ...Artwork_artwork
}

fragment Artwork_artwork on Artwork {
  title
  __id
}
