query indexTestsQuery {
  show(id: "anderson-fine-art-gallery-flickinger-collection") {
    ...Show_show
    __id
  }
}

fragment Show_show on Show {
  id
  name
  description
  press_release
  ...ShowHeader_show
  ...Location_show
  ...Artworks_show
  artists {
    __id
    id
    name
    is_followed
  }
  status
  counts {
    artworks
    eligible_artworks
  }
  partner {
    __typename
    ... on ExternalPartner {
      name
      __id
    }
    ... on Partner {
      name
    }
    ... on Node {
      __id
    }
  }
  __id
}

fragment ShowHeader_show on Show {
  name
  description
  press_release
  exhibition_period
  status
  images {
    url
    aspect_ratio
  }
  __id
}

fragment Location_show on Show {
  location {
    __id
    id
    city
    address
    address_2
    coordinates {
      lat
      lng
    }
    day_schedules {
      start_time
      end_time
      day_of_week
    }
  }
  partner {
    __typename
    ... on ExternalPartner {
      name
      __id
    }
    ... on Partner {
      name
    }
    ... on Node {
      __id
    }
  }
  __id
}

fragment Artworks_show on Show {
  __id
  artworks {
    ...GenericGrid_artworks
    __id
  }
}

fragment GenericGrid_artworks on Artwork {
  __id
  id
  image {
    aspect_ratio
  }
  ...Artwork_artwork
}

fragment Artwork_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  is_biddable
  is_acquireable
  id
  sale {
    is_auction
    is_live_open
    is_open
    is_closed
    display_timely_at
    __id
  }
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}
